input:
agent Collector {
    name: "Data Collector"
    capabilities [collect_data]
}

agent Processor {
    name: "Data Processor"
    capabilities [process_data]
}

agent Analyzer {
    name: "Data Analyzer"
    capabilities [analyze_data]
}

workflow DataPipeline {
    agents: [Collector, Processor, Analyzer]

    flow {
        Collector -> Processor
        Processor -> Analyzer
    }
}
